import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-ai-itinerary',
  templateUrl: './ai-itinerary.component.html',
  styleUrls: ['./ai-itinerary.component.css']
})
export class AiItineraryComponent {
  prompt: string = '';
  itinerary: string = '';

  constructor(private http: HttpClient) {}

  generateItinerary() {
    const body = { prompt: this.prompt };
    this.http.post('http://localhost:8089/api/ai/itinerary', body, { responseType: 'text' })
      .subscribe(response => {
        this.itinerary = response;
      });
  }
}
<div class="container">
  <h2>AI Itinerary Generator</h2>

  <label for="prompt">Describe Your Trip:</label>
  <textarea [(ngModel)]="prompt" id="prompt" rows="4" class="form-control"></textarea>

  <button (click)="generateItinerary()" class="btn btn-primary mt-2">Generate</button>

  <div *ngIf="itinerary" class="mt-4">
    <h4>Suggested Itinerary:</h4>
    <pre>{{ itinerary }}</pre>
  </div>
</div>
