import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService, RegisterRequest } from 'src/app/services/auth.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
})
export class RegisterComponent {
  registerRequest: RegisterRequest = {
    name: '',
    email: '',
    password: '',
    role: 'USER' // default role
  };

  constructor(private authService: AuthService, private router: Router) {}

  register(): void {
    this.authService.register(this.registerRequest).subscribe({
      next: () => {
        alert('Registration successful!');
        this.router.navigate(['/login']);
      },
      error: () => {
        alert('Registration failed. Try again.');
      }
    });
  }
}
<div class="container mt-5" style="max-width: 500px;">
  <h2 class="mb-4">Register</h2>

  <form (ngSubmit)="register()">
    <div class="mb-3">
      <label class="form-label">Name</label>
      <input class="form-control" [(ngModel)]="registerRequest.name" name="name" required />
    </div>

    <div class="mb-3">
      <label class="form-label">Email</label>
      <input type="email" class="form-control" [(ngModel)]="registerRequest.email" name="email" required />
    </div>

    <div class="mb-3">
      <label class="form-label">Password</label>
      <input type="password" class="form-control" [(ngModel)]="registerRequest.password" name="password" required />
    </div>

    <div class="mb-3">
      <label class="form-label">Role</label>
      <select class="form-select" [(ngModel)]="registerRequest.role" name="role">
        <option value="USER">User</option>
        <option value="ADMIN">Admin</option>
      </select>
    </div>

    <button class="btn btn-primary" type="submit">Register</button>
    <a routerLink="/login" class="btn btn-link">Already have an account? Login</a>
  </form>
</div>
