import { Component, OnInit } from '@angular/core';
import { AiTripService } from 'src/app/services/ai-trip.service';
import { AiTrip } from 'src/app/models/ai-trip.model';

@Component({
  selector: 'app-ai-trip-list',
  templateUrl: './ai-trip-list.component.html',
  styleUrls: ['./ai-trip-list.component.css']
})
export class AiTripListComponent implements OnInit {

  aiTrips: AiTrip[] = [];

  constructor(private aiTripService: AiTripService) {}

  ngOnInit(): void {
    this.getAiTrips();
  }

  getAiTrips(): void {
    const email = localStorage.getItem('email');
    if (email) {
      this.aiTripService.getTripsByEmail(email).subscribe((data: AiTrip[]) => {
        this.aiTrips = data;
      });
    }
  }

  deleteTrip(id: number): void {
    this.aiTripService.deleteTrip(id).subscribe(() => {
      this.aiTrips = this.aiTrips.filter(trip => trip.id !== id);
    });
  }
}
<div class="container mt-4">
  <h2>Your AI-Generated Trips</h2>

  <div *ngIf="aiTrips.length === 0">
    <p>No AI trips saved yet.</p> <!-- ✅ open and close together -->
  </div>

  <div *ngFor="let trip of aiTrips" class="card mb-3">
    <div class="card-body">
      <h5 class="card-title">{{ trip.destination }}</h5>
      <p class="card-text"><strong>Start Date:</strong> {{ trip.startDate | date }}</p>
      <p class="card-text"><strong>End Date:</strong> {{ trip.endDate | date }}</p>
      <p class="card-text"><strong>Itinerary:</strong> {{ trip.itinerary }}</p>
      <button (click)="deleteTrip(trip.id)" class="btn btn-danger btn-sm">Delete</button>
    </div>
  </div>
</div>
