import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-generate-itinerary',
  templateUrl: './generate-itinerary.component.html',
  styleUrls: ['./generate-itinerary.component.css']
})
export class GenerateItineraryComponent {
  destination = '';
  startDate = '';
  endDate = '';
  result = '';
  loading = false;

  constructor(private http: HttpClient) {}

  generate() {
    if (!this.destination || !this.startDate || !this.endDate) {
      alert('Please fill all fields');
      return;
    }

    this.loading = true;

    const body = {
      destination: this.destination,
      startDate: this.startDate,
      endDate: this.endDate
    };

    this.http.post('http://localhost:8089/api/gemini/generate-itinerary', body, { responseType: 'text' })
      .subscribe({
        next: (data) => {
          this.result = data;
          this.loading = false;
        },
        error: (err) => {
          console.error('Error generating itinerary:', err);
          this.result = 'Error generating itinerary. Please try again later.';
          this.loading = false;
        }
      });
  }
}
<app-user-navbar></app-user-navbar>

<div class="container mt-4">
  <h2>AI Trip Itinerary Generator</h2>

  <form (ngSubmit)="generate()">
    <div class="mb-3">
      <label class="form-label">Destination</label>
      <input class="form-control" [(ngModel)]="destination" name="destination" required />
    </div>

    <div class="mb-3">
      <label class="form-label">Start Date</label>
      <input type="date" class="form-control" [(ngModel)]="startDate" name="startDate" required />
    </div>

    <div class="mb-3">
      <label class="form-label">End Date</label>
      <input type="date" class="form-control" [(ngModel)]="endDate" name="endDate" required />
    </div>

    <button type="submit" class="btn btn-success" [disabled]="loading">
      {{ loading ? 'Generating...' : 'Generate Itinerary' }}
    </button>
  </form>

  <div *ngIf="result" class="mt-4">
    <h4>Generated Itinerary:</h4>
    <pre>{{ result }}</pre>
  </div>
</div>
