import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-user-navbar',
  templateUrl: './user-navbar.component.html'
})
export class UserNavbarComponent {
  showDropdown = false;
  username = localStorage.getItem('name');
  email = localStorage.getItem('email');

  constructor(private authService: AuthService, private router: Router) {}

  toggleDropdown(): void {
    this.showDropdown = !this.showDropdown;
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<nav style="display: flex; justify-content: space-between; align-items: center; padding: 10px 20px; background-color: #1976d2; color: white;">
  <div style="font-size: 20px;">User Dashboard</div>

  <div style="display: flex; gap: 20px;">
    <a routerLink="/add-trip" style="color: white; text-decoration: none;">Add Trip</a>
    <a routerLink="/trip-list" style="color: white; text-decoration: none;">View Trips</a>
    <a class="nav-link" routerLink="/generate-itinerary">AI Itinerary</a>

  </div>

  <div style="position: relative;">
    <img src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" alt="Profile"
         (click)="toggleDropdown()" style="width: 40px; height: 40px; border-radius: 50%; cursor: pointer;" />

    <div *ngIf="showDropdown" style="position: absolute; right: 0; top: 50px; background: white; color: black; border: 1px solid #ccc; border-radius: 8px; padding: 12px; min-width: 220px; z-index: 100;">
      <p><strong>User:</strong> {{ username }}</p>
      <p><strong>Email:</strong> {{ email }}</p>
      <button (click)="logout()" style="background-color: #d32f2f; color: white; padding: 6px 12px; border: none; border-radius: 4px; cursor: pointer;">
        Logout
      </button>
    </div>
  </div>
</nav>
