// ✅ Updated TripService with userEmail check
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface Trip {
  id?: number;
  tripName: string;
  destination: string;
  startDate: string;
  endDate: string;
  budget: number;
  notes: string;
}

@Injectable({
  providedIn: 'root'
})
export class TripService {
  private baseUrl = 'http://localhost:8089/api/trips';
  private aiUrl = 'http://localhost:8089/api/ai/itinerary';

  constructor(private http: HttpClient) {}

  private getUserEmail(): string {
    const email = localStorage.getItem('userEmail');
    if (!email) {
      throw new Error("User email not found in localStorage.");
    }
    return email;
  }

  getAllTrips(): Observable<Trip[]> {
    const email = this.getUserEmail();
    return this.http.get<Trip[]>(`${this.baseUrl}/${email}`);
  }

  getTripById(id: number): Observable<Trip> {
    const email = this.getUserEmail();
    return this.http.get<Trip>(`${this.baseUrl}/${id}/${email}`);
  }

  createTrip(trip: Trip): Observable<Trip> {
    const email = this.getUserEmail();
    return this.http.post<Trip>(`${this.baseUrl}/${email}`, trip);
  }

  updateTrip(id: number, trip: Trip): Observable<Trip> {
    const email = this.getUserEmail();
    return this.http.put<Trip>(`${this.baseUrl}/${id}/${email}`, trip);
  }

  deleteTrip(id: number): Observable<void> {
    const email = this.getUserEmail();
    return this.http.delete<void>(`${this.baseUrl}/${id}/${email}`);
  }

  addTrip(trip: Trip): Observable<Trip> {
    return this.createTrip(trip);
  }

  generateAiItinerary(prompt: string): Observable<string> {
    return this.http.post(this.aiUrl, { prompt }, { responseType: 'text' });
  }
}
