import { Component, OnInit } from '@angular/core';
import { Trip, TripService } from 'src/app/services/trip.service';

@Component({
  selector: 'app-trip-list',
  templateUrl: './trip-list.component.html',
})
export class TripListComponent implements OnInit {
  trips: Trip[] = [];

  constructor(private tripService: TripService) {}

  ngOnInit(): void {
    this.loadTrips();
  }

  loadTrips(): void {
    this.tripService.getAllTrips().subscribe(data => {
      this.trips = data;
    });
  }

  deleteTrip(id: number): void {
    if (confirm('Are you sure you want to delete this trip?')) {
      this.tripService.deleteTrip(id).subscribe(() => {
        this.loadTrips();
      });
    }
  }
}
<div class="container mt-4">
  <h2>All Trips</h2>
  <a routerLink="/add-trip" class="btn btn-success mb-3">Add New Trip</a>
  <table class="table table-bordered">
    <thead>
      <tr>
        <th>ID</th>
        <th>Trip Name</th>
        <th>Destination</th>
        <th>Start Date</th>
        <th>End Date</th>
        <th>Notes</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let trip of trips">
        <td>{{ trip.id }}</td>
        <td>{{ trip.tripName }}</td>
        <td>{{ trip.destination }}</td>
        <td>{{ trip.startDate }}</td>
        <td>{{ trip.endDate }}</td>
        <td>{{ trip.notes }}</td>
        <td>
          <a [routerLink]="['/edit-trip', trip.id]" class="btn btn-warning btn-sm">Edit</a>
          <button (click)="deleteTrip(trip.id!)" class="btn btn-danger btn-sm ms-2">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
