import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router'; // ✅ import Router
import { AiTrip } from 'src/app/models/ai-trip.model';
import { AiTripService } from 'src/app/services/ai-trip.service';

@Component({
  selector: 'app-ai-trip-list',
  templateUrl: './ai-trip-list.component.html',
  styleUrls: ['./ai-trip-list.component.css']
})
export class AiTripListComponent implements OnInit {
  trips: AiTrip[] = [];
  isLoading = true;

  constructor(
    private tripService: AiTripService,
    private router: Router // ✅ inject Router
  ) {}

  ngOnInit(): void {
    this.tripService.getAllTrips().subscribe({
      next: (data) => {
        this.trips = data;
        this.isLoading = false;
      },
      error: (err) => {
        console.error('Failed to load trips', err);
        this.isLoading = false;
      }
    });
  }

  // ✅ Navigate to details page
  viewTrip(id: number) {
    this.router.navigate(['/Ai-trip-details', id]);
  }
}
<div class="container mt-4">
  <h2>Saved AI Trips</h2>

  <div *ngIf="isLoading">Loading trips...</div>
  <div *ngIf="!isLoading && trips.length === 0">No saved trips yet.</div>

  <div *ngFor="let trip of trips" class="card my-3 shadow-sm">
    <div class="card-body">
      <h5 class="card-title">{{ trip.destination }}</h5>
      <p class="card-text">
        {{ trip.startDate | date }} - {{ trip.endDate | date }}
      </p>
      <button class="btn btn-primary" (click)="viewTrip(trip.id)">View Details</button>
    </div>
  </div>
</div>
