import { Component } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html'
})
export class LoginComponent {
  credentials = {
    email: '',
    password: ''
  };

  constructor(private authService: AuthService, private router: Router) {}

  loginUser() {
    this.authService.login(this.credentials).subscribe({
      next: (res: any) => {
        localStorage.setItem('token', res.token || 'dummy-token');
        localStorage.setItem('role', res.role);

        // ✅ Store name and email properly (handle both res.user and res.username/email)
        localStorage.setItem('name', res.user?.name || res.username || '');
        localStorage.setItem('email', res.user?.email || res.email || '');

        alert('Login successful!');

        if (res.role === 'ADMIN') {
          this.router.navigate(['/admin-dashboard']);
        } else if (res.role === 'USER') {
          this.router.navigate(['/user-dashboard']);
        } else {
          this.router.navigate(['/']);
        }
      },
      error: err => {
        alert('Login failed. Please check your credentials.');
        console.error(err);
      }
    });
  }
}
<div class="container mt-5 col-md-6 animate__animated animate__fadeIn">
  <div class="card shadow-lg rounded-4 border-0">
    <div class="card-body p-4">
      <h2 class="text-center mb-4 fw-bold text-success">
        <i class="bi bi-box-arrow-in-right me-2"></i>Login
      </h2>

      <form (submit)="loginUser()" novalidate>
        <div class="form-group mb-3">
          <label class="fw-semibold"><i class="bi bi-envelope-fill me-2"></i>Email</label>
          <input [(ngModel)]="credentials.email" name="email" type="email" class="form-control form-control-lg shadow-sm" required>
        </div>

        <div class="form-group mb-4">
          <label class="fw-semibold"><i class="bi bi-lock-fill me-2"></i>Password</label>
          <input [(ngModel)]="credentials.password" name="password" type="password" class="form-control form-control-lg shadow-sm" required>
        </div>

        <button class="btn btn-success w-100 btn-lg rounded-pill shadow-sm" type="submit">
          <i class="bi bi-check-circle-fill me-1"></i>Login
        </button>
      </form>

      <div class="mt-4 text-center">
        <p class="mb-0">Don't have an account? 
          <a routerLink="/register" class="text-decoration-none fw-semibold text-success hover-underline">
            <i class="bi bi-person-plus-fill me-1"></i>Register here
          </a>
        </p>
      </div>
    </div>
  </div>
</div>
