// âœ… Updated login.component.ts with correct localStorage key
import { Component } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html'
})
export class LoginComponent {
  credentials = {
    email: '',
    password: ''
  };

  constructor(private authService: AuthService, private router: Router) {}

  loginUser() {
    this.authService.login(this.credentials).subscribe({
      next: (res: any) => {
        localStorage.setItem('token', res.token || 'dummy-token');
        localStorage.setItem('role', res.role);

        const email = res.user?.email || res.email || '';
        const name = res.user?.name || res.username || '';

        // âœ… Store email using the correct key expected by TripService
        localStorage.setItem('email', email);
        localStorage.setItem('userEmail', email); // ðŸ‘ˆ this is required for trip.service.ts

        localStorage.setItem('name', name);

        alert('Login successful!');

        if (res.role === 'ADMIN') {
          this.router.navigate(['/admin-dashboard']);
        } else if (res.role === 'USER') {
          this.router.navigate(['/user-dashboard']);
        } else {
          this.router.navigate(['/']);
        }
      },
      error: err => {
        alert('Login failed. Please check your credentials.');
        console.error(err);
      }
    });
  }
}
<div class="container mt-5 col-md-6">
  <h2>Login</h2>
  <form (submit)="loginUser()">
    <div class="form-group mb-2">
      <label>Email</label>
      <input [(ngModel)]="credentials.email" name="email" type="email" class="form-control" required>
    </div>
    <div class="form-group mb-2">
      <label>Password</label>
      <input [(ngModel)]="credentials.password" name="password" type="password" class="form-control" required>
    </div>
    <button class="btn btn-success mt-2" type="submit">Login</button>
  </form>

  <!-- âœ… Register Link -->
  <div class="mt-3">
    <p>Don't have an account? 
      <a routerLink="/register" class="text-primary">Register here</a>
    </p>
  </div>
</div>
