import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-user-navbar',
  templateUrl: './user-navbar.component.html'
})
export class UserNavbarComponent {
  showDropdown = false;
  username = localStorage.getItem('name');
  email = localStorage.getItem('email');

  constructor(private authService: AuthService, private router: Router) {}

  toggleDropdown(): void {
    this.showDropdown = !this.showDropdown;
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<nav class="user-navbar">
  <div class="navbar-title">User Dashboard</div>

  <div class="navbar-links" [class.show]="showDropdown">
    <a routerLink="/add-trip">Add Trip</a>
    <a routerLink="/trip-list">View Trips</a>
    <a routerLink="/generate-itinerary">AI Itinerary</a>
    <a routerLink="/Ai-trip-list">AI Saved Trips</a>
  </div>

  <!-- Hamburger Menu Icon for small screens -->
  <div class="menu-icon" (click)="toggleDropdown()">
    <i class="bi bi-list"></i>
  </div>

  <div class="navbar-profile">
    <img src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" alt="Profile"
         (click)="toggleDropdown()" />

    <div *ngIf="showDropdown" class="dropdown-box">
      <p><strong>User:</strong> {{ username }}</p>
      <p><strong>Email:</strong> {{ email }}</p>
      <button (click)="logout()">Logout</button>
    </div>
  </div>
</nav>
