import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-ai-trip-details',
  templateUrl: './ai-trip-details.component.html',
  styleUrls: ['./ai-trip-details.component.css']
})
export class AiTripDetailsComponent implements OnInit {
  trip: any;
  isLoading = true;

  constructor(private route: ActivatedRoute, private http: HttpClient) {}

  ngOnInit(): void {
    const tripId = this.route.snapshot.paramMap.get('id');

    this.http.get(`http://localhost:8084/api/ai-trips/${tripId}`).subscribe({
      next: (data) => {
        this.trip = data;
        this.isLoading = false;
      },
      error: (err) => {
        console.error('Error fetching trip:', err);
        this.isLoading = false;
      }
    });
  }
}
<div class="container mt-4" *ngIf="!isLoading && trip">
  <h2>{{ trip.destination }}</h2>
  <p>{{ trip.startDate | date }} - {{ trip.endDate | date }}</p>

  <hr />

  <div [innerHTML]="trip.itinerary" class="border p-3 rounded bg-light"></div>

  <button class="btn btn-secondary mt-3" routerLink="/Ai-trip-list">Back to List</button>
</div>
