import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { AiTripService } from 'src/app/services/ai-trip.service';
import { AiTrip } from 'src/app/models/ai-trip.model';

@Component({
  selector: 'app-edit-ai-trip',
  templateUrl: './edit-ai-trip.component.html',
  styleUrls: ['./edit-ai-trip.component.css']
})
export class EditAiTripComponent implements OnInit {
  tripId!: number;
  trip: AiTrip = {
    id: 0,
    destination: '',
    days: 0,
    budget: '',
    travellers: '',
    tripPlan: ''
  };

  constructor(
    private route: ActivatedRoute,
    private tripService: AiTripService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.tripId = Number(this.route.snapshot.paramMap.get('id'));
    this.tripService.getTripById(this.tripId).subscribe({
      next: (data) => {
        this.trip = data;
      },
      error: (error: any) => {
        console.error('Error fetching trip:', error);
      }
    });
  }

  onUpdateTrip(): void {
    this.tripService.updateTrip(this.tripId, this.trip).subscribe({
      next: () => {
        this.router.navigate(['/saved-ai-trips']);
      },
      error: (err: any) => {
        console.error('Update failed', err);
      }
    });
  }
}
<app-user-navbar></app-user-navbar>
<div class="container mt-4">
  <h2>Edit Trip</h2>

  <form (ngSubmit)="onUpdateTrip()">
    <div class="form-group mb-3">
      <label for="destination">Destination</label>
      <input
        type="text"
        id="destination"
        class="form-control"
        [(ngModel)]="trip.destination"
        name="destination"
        required
      />
    </div>

    <div class="form-group mb-3">
      <label for="days">Number of Days</label>
      <input
        type="number"
        id="days"
        class="form-control"
        [(ngModel)]="trip.days"
        name="days"
        required
      />
    </div>

    <div class="form-group mb-3">
      <label for="budget">Budget</label>
      <input
        type="text"
        id="budget"
        class="form-control"
        [(ngModel)]="trip.budget"
        name="budget"
        required
      />
    </div>

    <div class="form-group mb-3">
      <label for="travellers">Travellers</label>
      <input
        type="text"
        id="travellers"
        class="form-control"
        [(ngModel)]="trip.travellers"
        name="travellers"
        required
      />
    </div>

    <div class="form-group mb-3">
      <label for="tripPlan">Trip Plan</label>
      <textarea
        id="tripPlan"
        class="form-control"
        rows="6"
        [(ngModel)]="trip.tripPlan"
        name="tripPlan"
        required
      ></textarea>
    </div>

    <button class="btn btn-success" type="submit">Update Trip</button>
  </form>
</div>
