// src/app/pages/trip-list/trip-list.component.ts
import { Component, OnInit } from '@angular/core';
import { TripService } from 'src/app/services/trip.service';
import { Trip } from 'src/app/models/trip.model';
import { Router } from '@angular/router';

@Component({
  selector: 'app-trip-list',
  templateUrl: './trip-list.component.html',
  styleUrls: ['./trip-list.component.css']
})
export class TripListComponent implements OnInit {
  trips: Trip[] = [];
  userEmail: string = '';

  constructor(private tripService: TripService, private router: Router) {}

  ngOnInit(): void {
    const email = localStorage.getItem('email');
    if (email) {
      this.userEmail = email;
      this.loadTrips();
    }
  }

  loadTrips(): void {
    this.tripService.getTripsByUser(this.userEmail).subscribe((data: Trip[]) => {
      this.trips = data;
    });
  }

  deleteTrip(id: number): void {
    if (confirm('Are you sure you want to delete this trip?')) {
      this.tripService.deleteTrip(id).subscribe(() => this.loadTrips());
    }
  }

  editTrip(id: number): void {
    this.router.navigate(['/edit-trip', id]);
  }
}
<app-user-navbar></app-user-navbar>

<div class="container mt-5">
  <div class="card shadow-lg rounded p-4 animate__animated animate__fadeIn">
    <h2 class="mb-4 text-primary">
      <i class="bi bi-list-ul me-2"></i>Your Trips
    </h2>

    <div class="table-responsive">
      <table class="table table-bordered table-hover align-middle text-center">
        <thead class="table-dark">
          <tr>
            <th><i class="bi bi-card-text"></i> Trip Name</th>
            <th><i class="bi bi-geo-alt-fill"></i> Destination</th>
            <th><i class="bi bi-calendar-event"></i> Start Date</th>
            <th><i class="bi bi-calendar-check"></i> End Date</th>
            <th><i class="bi bi-cash-stack"></i> Budget (₹)</th>
            <th><i class="bi bi-truck-front-fill"></i> Transportation</th>
            <th><i class="bi bi-journal-text"></i> Notes</th>
            <th><i class="bi bi-gear"></i> Actions</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let trip of trips">
            <td>{{ trip.tripName }}</td>
            <td>{{ trip.destination }}</td>
            <td>{{ trip.startDate }}</td>
            <td>{{ trip.endDate }}</td>
            <td>{{ trip.budget }}</td>
            <td>{{ trip.transportation }}</td>
            <td>{{ trip.notes }}</td>
            <td>
              <div class="d-flex justify-content-center gap-2">
                <button class="btn btn-warning btn-sm shadow-sm" (click)="editTrip(trip.id!)">
                  <i class="bi bi-pencil-square"></i>
                </button>
                <button class="btn btn-danger btn-sm shadow-sm" (click)="deleteTrip(trip.id!)">
                  <i class="bi bi-trash3-fill"></i>
                </button>
              </div>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
</div>
