import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface Trip {
  id?: number;
  tripName: string;
  destination: string;
  startDate: string; // ISO string format
  endDate: string;
  budget: number;
  notes: string;
}

@Injectable({
  providedIn: 'root'
})
export class TripService {
  private baseUrl = 'http://localhost:8089/api/trips';
  private aiUrl = 'http://localhost:8089/api/ai/itinerary';

  constructor(private http: HttpClient) {}

  // ✅ Updated: now includes headers with role & email
  getAllTrips(): Observable<Trip[]> {
    const headers = new HttpHeaders({
      email: localStorage.getItem('email') || '',
      role: localStorage.getItem('role') || ''
    });

    return this.http.get<Trip[]>(`${this.baseUrl}`, { headers });
  }

  getTripById(id: number): Observable<Trip> {
    return this.http.get<Trip>(`${this.baseUrl}/${id}`);
  }

  createTrip(trip: Trip): Observable<Trip> {
    return this.http.post<Trip>(`${this.baseUrl}`, trip);
  }

  updateTrip(id: number, trip: Trip): Observable<Trip> {
    return this.http.put<Trip>(`${this.baseUrl}/${id}`, trip);
  }

  deleteTrip(id: number): Observable<void> {
    return this.http.delete<void>(`${this.baseUrl}/${id}`);
  }

  // Optional alias to avoid changing component code
  addTrip(trip: Trip): Observable<Trip> {
    return this.createTrip(trip);
  }

  // ✅ Call AI itinerary generator
  generateAiItinerary(prompt: string): Observable<string> {
    return this.http.post(this.aiUrl, { prompt }, { responseType: 'text' });
  }
}
