import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';
// import { AuthService } from '../auth.service';

@Component({
  selector: 'app-user-dashboard',
  templateUrl: './user-dashboard.component.html'
})
export class UserDashboardComponent {
  constructor(private authService: AuthService, private router: Router) {}

  logout() {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<nav style="display: flex; justify-content: space-between; padding: 10px; background-color: #1976d2; color: white;">
  <div style="font-size: 20px;">Dashboard</div>

  <div style="position: relative;">
    <img 
      src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png"
      alt="Profile"
      (click)="toggleDropdown()"
      style="width: 40px; height: 40px; border-radius: 50%; cursor: pointer;"
    />

    <!-- Dropdown -->
    <div *ngIf="showDropdown" 
         style="position: absolute; right: 0; top: 50px; background: white; color: black; border: 1px solid #ccc; border-radius: 8px; padding: 10px; min-width: 200px; z-index: 10;">
      <p><strong>User:</strong> {{ username }}</p>
      <p><strong>Email:</strong> {{ email }}</p>
      <button (click)="logout()" style="background-color: red; color: white; padding: 5px 10px; border: none; border-radius: 4px; cursor: pointer;">
        Logout
      </button>
    </div>
  </div>
</nav>
