import { Component, OnInit } from '@angular/core';
import { AiTrip } from 'src/app/models/ai-trip.model';
import { AiTripService } from 'src/app/services/ai-trip.service';

@Component({
  selector: 'app-ai-trip-list',
  templateUrl: './ai-trip-list.component.html',
  styleUrls: ['./ai-trip-list.component.css']
})
export class AiTripListComponent implements OnInit {
  trips: AiTrip[] = [];
  isLoading = true;

  constructor(private tripService: AiTripService) {}

  ngOnInit(): void {
    this.tripService.getAllTrips().subscribe({
      next: (data) => {
        // Add `showDetails` flag for toggle
        this.trips = data.map(trip => ({
          ...trip,
          showDetails: false
        }));
        this.isLoading = false;
      },
      error: (err) => {
        console.error('Failed to load trips', err);
        this.isLoading = false;
      }
    });
  }

  toggleTrip(trip: AiTrip): void {
    trip.showDetails = !trip.showDetails;
  }
}
<app-user-navbar></app-user-navbar>
<div style="max-width: 900px; margin: auto; padding: 20px;">
  <h2>Saved AI Trips</h2>

  <div *ngIf="isLoading">Loading trips...</div>
  <div *ngIf="!isLoading && trips.length === 0">No saved trips yet.</div>

  <div *ngFor="let trip of trips" class="card my-3 shadow-sm">
    <div class="card-body">
      <h4 class="card-title">{{ trip.destination }}</h4>
      <p><strong>From:</strong> {{ trip.startDate }} <strong>To:</strong> {{ trip.endDate }}</p>
      <div [innerHTML]="trip.itinerary" class="border p-2 rounded bg-light"></div>
    </div>
  </div>
</div>
