import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-admin-navbar',
  templateUrl: './admin-navbar.component.html'
})
export class AdminNavbarComponent {
  showDropdown = false;
  username = localStorage.getItem('name');
  email = localStorage.getItem('email');

  constructor(private authService: AuthService, private router: Router) {}

  toggleDropdown(): void {
    this.showDropdown = !this.showDropdown;
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<nav class="navbar navbar-expand-lg navbar-dark bg-primary px-4 shadow-sm">
  <div class="container-fluid">
    <a class="navbar-brand fw-bold fs-4" href="#">Admin Dashboard</a>

    <div class="d-flex align-items-center gap-4 flex-wrap">
      <a routerLink="/admin/users" class="nav-link text-white d-flex align-items-center">
        <i class="bi bi-people-fill me-1"></i> Manage Users
      </a>
      <a routerLink="/admin/trips" class="nav-link text-white d-flex align-items-center">
        <i class="bi bi-suitcase-lg-fill me-1"></i> Manage Trips
      </a>
    </div>

    <div class="dropdown ms-auto">
      <img src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" alt="Profile"
        class="rounded-circle" width="40" height="40" style="cursor: pointer;" (click)="toggleDropdown()" />

      <div *ngIf="showDropdown" class="dropdown-menu dropdown-menu-end show mt-2 shadow-sm p-3 rounded-3 animate__animated animate__fadeIn" style="min-width: 220px;">
        <p class="mb-2"><strong>User:</strong> {{ username }}</p>
        <p class="mb-3"><strong>Email:</strong> {{ email }}</p>
        <button class="btn btn-danger w-100 btn-sm rounded-pill" (click)="logout()">
          <i class="bi bi-box-arrow-right me-1"></i>Logout
        </button>
      </div>
    </div>
  </div>
</nav>
