import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-ai-trip-list',
  templateUrl: './ai-trip-list.component.html'
})
export class AiTripListComponent implements OnInit {
  trips: any[] = [];

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    const userEmail = localStorage.getItem('email');
    this.http.get<any[]>(`http://localhost:8084/api/ai-trips/user/${userEmail}`)
      .subscribe({
        next: (data) => {
          this.trips = data;
        },
        error: (err) => {
          console.error('Error fetching AI trips:', err);
        }
      });
  }

  deleteTrip(id: number) {
    if (confirm('Are you sure you want to delete this trip?')) {
      this.http.delete(`http://localhost:8084/api/ai-trips/${id}`, { responseType: 'text' })
        .subscribe({
          next: () => {
            alert('Trip deleted.');
            this.trips = this.trips.filter(trip => trip.id !== id);
          },
          error: (err) => {
            console.error('Delete Error:', err);
          }
        });
    }
  }
}
