<app-user-navbar></app-user-navbar>

<div class="container mt-4">
  <h2>Your Saved AI Trips</h2>

  <div *ngIf="trips.length === 0">
    <p>No AI trips found.</p>
  </div>

  <div *ngFor="let trip of trips" class="card mb-3">
    <div class="card-body">
      <h5 class="card-title">{{ trip.destination }}</h5>
      <p class="card-text">
        <strong>Start Date:</strong> {{ trip.startDate }}<br>
        <strong>End Date:</strong> {{ trip.endDate }}
      </p>
      <div [innerHTML]="trip.itinerary"></div>

      <button class="btn btn-danger mt-2" (click)="deleteTrip(trip.id)">Delete</button>
    </div>
  </div>
</div>
