// src/app/components/add-trip/add-trip.component.ts

import { Component, OnInit } from '@angular/core';
import { TripService, Trip } from 'src/app/services/trip.service';
import { Router, ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-add-trip',
  templateUrl: './add-trip.component.html'
})
export class AddTripComponent implements OnInit {
  trip: Trip = {
    tripName: '',
    destination: '',
    startDate: '',
    endDate: '',
    email: '' // will be filled from localStorage
  };

  isEditMode = false;
  tripId!: number;

  constructor(
    private tripService: TripService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.trip.email = localStorage.getItem('userEmail') || '';

    this.route.params.subscribe(params => {
      if (params['id']) {
        this.isEditMode = true;
        this.tripId = +params['id'];
        this.tripService.getTripById(this.tripId).subscribe(data => {
          this.trip = data;
        });
      }
    });
  }

  onSubmit(): void {
    if (this.isEditMode) {
      this.tripService.updateTrip(this.tripId, this.trip).subscribe(() => {
        this.router.navigate(['/user-dashboard']);
      });
    } else {
      this.tripService.addTrip(this.trip).subscribe(() => {
        this.router.navigate(['/user-dashboard']);
      });
    }
  }
}
