import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-generate-itinerary',
  templateUrl: './generate-itinerary.component.html',
  styleUrls: ['./generate-itinerary.component.css']
})
export class GenerateItineraryComponent {
  destination = '';
  startDate = '';
  endDate = '';
  result = '';
  loading = false;

  constructor(private http: HttpClient) {}

  generate() {
    if (!this.destination || !this.startDate || !this.endDate) {
      alert('Please fill all fields');
      return;
    }

    this.loading = true;
    const body = {
      destination: this.destination,
      startDate: this.startDate,
      endDate: this.endDate
    };

    this.http.post('http://localhost:8089/api/gemini/generate-itinerary', body, { responseType: 'text' })
      .subscribe({
        next: (data) => {
          this.result = data;
          this.loading = false;
        },
        error: (err) => {
          this.result = 'Error generating itinerary.';
          this.loading = false;
        }
      });
  }
}

<div class="container mt-4">
  <h2>AI Travel Itinerary Generator</h2>

  <div class="mb-3">
    <label class="form-label">Destination</label>
    <input type="text" class="form-control" [(ngModel)]="destination" />
  </div>

  <div class="mb-3">
    <label class="form-label">Start Date</label>
    <input type="date" class="form-control" [(ngModel)]="startDate" />
  </div>

  <div class="mb-3">
    <label class="form-label">End Date</label>
    <input type="date" class="form-control" [(ngModel)]="endDate" />
  </div>

  <button class="btn btn-success" (click)="generate()" [disabled]="loading">
    {{ loading ? 'Generating...' : 'Generate Itinerary' }}
  </button>

  <div class="mt-4" *ngIf="result">
    <h4>Suggested Itinerary:</h4>
    <pre>{{ result }}</pre>
  </div>
</div>
