<div class="container mt-4">
  <h2>Your Trips</h2>

  <table class="table table-bordered table-hover mt-3">
    <thead class="thead-dark">
      <tr>
        <th>Trip Name</th>
        <th>Destination</th>
        <th>Start Date</th>
        <th>End Date</th>
        <th>Budget</th>
        <th>Transportation</th>
        <th>Notes</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let trip of trips">
        <td>{{ trip.tripName }}</td>
        <td>{{ trip.destination }}</td>
        <td>{{ trip.startDate }}</td>
        <td>{{ trip.endDate }}</td>
        <td>{{ trip.budget }}</td>
        <td>{{ trip.transportation }}</td>
        <td>{{ trip.notes }}</td>
        <td>
          <button class="btn btn-warning btn-sm" (click)="editTrip(trip.id!)">Edit</button>
          <button class="btn btn-danger btn-sm" (click)="deleteTrip(trip.id!)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
