// src/app/components/ai-trip-list/ai-trip-list.component.ts
import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface AiTrip {
  id: number;
  destination: string;
  startDate: string;
  endDate: string;
  itinerary: string;
}

@Component({
  selector: 'app-ai-trip-list',
  templateUrl: './ai-trip-list.component.html'
})
export class AiTripListComponent implements OnInit {
  aiTrips: AiTrip[] = [];
  userEmail: string | null = '';

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.userEmail = localStorage.getItem('email');
    if (this.userEmail) {
      this.fetchAiTrips(this.userEmail);
    }
  }

  fetchAiTrips(email: string) {
    this.http.get<AiTrip[]>(`http://localhost:8084/api/ai-trips/user/${email}`).subscribe({
      next: (data) => {
        this.aiTrips = data;
      },
      error: (err) => {
        console.error('Failed to fetch AI trips', err);
      }
    });
  }
}
