import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService, LoginRequest } from 'src/app/services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
})
export class LoginComponent {
  loginRequest: LoginRequest = {
    email: '',
    password: ''
  };

  constructor(private authService: AuthService, private router: Router) {}

  login(): void {
    this.authService.login(this.loginRequest).subscribe({
      next: (res) => {
        localStorage.setItem('token', res.token);
        localStorage.setItem('email', res.email);
        localStorage.setItem('name', res.name);
        localStorage.setItem('role', res.role);

        if (res.role === 'ADMIN') {
          this.router.navigate(['/admin-dashboard']);
        } else {
          this.router.navigate(['/user-dashboard']);
        }
      },
      error: () => {
        alert('Login failed. Please check your credentials.');
      }
    });
  }
}
<div class="container mt-5 col-md-6">
  <h2>Login</h2>
  <form (submit)="loginUser()">
    <div class="form-group mb-2">
      <label>Email</label>
      <input [(ngModel)]="credentials.email" name="email" type="email" class="form-control" required>
    </div>
    <div class="form-group mb-2">
      <label>Password</label>
      <input [(ngModel)]="credentials.password" name="password" type="password" class="form-control" required>
    </div>
    <button class="btn btn-success mt-2" type="submit">Login</button>
  </form>

  <!-- ✅ Register Link -->
  <div class="mt-3">
    <p>Don't have an account? 
      <a routerLink="/register" class="text-primary">Register here</a>
    </p>
  </div>
</div>
