import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Trip, TripService } from 'src/app/services/trip.service';

@Component({
  selector: 'app-add-trip',
  templateUrl: './add-trip.component.html',
})
export class AddTripComponent implements OnInit {
  trip: Trip = {
    tripName: '',
    destination: '',
    startDate: '',
    endDate: '',
    budget: 0,
    notes: ''
  };

  isEditMode: boolean = false;
  tripId: number | null = null;

  constructor(
    private tripService: TripService,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      const id = params.get('id');
      if (id) {
        this.isEditMode = true;
        this.tripId = +id;
        this.tripService.getTripById(this.tripId).subscribe(data => {
          // Convert date strings if needed
          this.trip = {
            ...data,
            startDate: data.startDate?.split('T')[0],
            endDate: data.endDate?.split('T')[0],
          };
        });
      }
    });
  }

  saveTrip(): void {
    if (this.isEditMode && this.tripId != null) {
      this.tripService.updateTrip(this.tripId, this.trip).subscribe(() => {
        alert('Trip updated successfully!');
        this.router.navigate(['/trip-list']);
      });
    } else {
      this.tripService.addTrip(this.trip).subscribe(() => {
        alert('Trip added successfully!');
        this.router.navigate(['/trip-list']);
      });
    }
  }
}
<div class="container mt-4">
  <h2>{{ isEditMode ? 'Edit Trip' : 'Add Trip' }}</h2>

  <form (ngSubmit)="saveTrip()">
    <div class="mb-3">
      <label class="form-label">Trip Name</label>
      <input class="form-control" [(ngModel)]="trip.tripName" name="tripName" required />
    </div>

    <div class="mb-3">
      <label class="form-label">Destination</label>
      <input class="form-control" [(ngModel)]="trip.destination" name="destination" required />
    </div>

    <div class="mb-3">
      <label class="form-label">Start Date</label>
      <input type="date" class="form-control" [(ngModel)]="trip.startDate" name="startDate" required />
    </div>

    <div class="mb-3">
      <label class="form-label">End Date</label>
      <input type="date" class="form-control" [(ngModel)]="trip.endDate" name="endDate" required />
    </div>

    <div class="mb-3">
      <label class="form-label">Budget</label>
      <input type="number" class="form-control" [(ngModel)]="trip.budget" name="budget" required />
    </div>

    <div class="mb-3">
      <label class="form-label">Notes</label>
      <textarea class="form-control" [(ngModel)]="trip.notes" name="notes"></textarea>
    </div>

    <button class="btn btn-primary" type="submit">
      {{ isEditMode ? 'Update Trip' : 'Add Trip' }}
    </button>
  </form>
</div>
