import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-user-navbar',
  templateUrl: './user-navbar.component.html'
})
export class UserNavbarComponent {
  showDropdown = false;
  username = localStorage.getItem('name');
  email = localStorage.getItem('email');

  constructor(private authService: AuthService, private router: Router) {}

  toggleDropdown(): void {
    this.showDropdown = !this.showDropdown;
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<nav class="navbar navbar-expand-lg navbar-dark bg-primary px-4 py-2">
  <div class="container-fluid">
    <a class="navbar-brand fw-bold" href="#">User Dashboard</a>

    <!-- Mobile menu toggle -->
    <button class="navbar-toggler" type="button" (click)="toggleNavbar()" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>

    <!-- Navigation links -->
    <div [ngClass]="{'collapse navbar-collapse': true, 'show': navbarOpen}" id="navbarNav">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item">
          <a routerLink="/add-trip" class="nav-link">Add Trip</a>
        </li>
        <li class="nav-item">
          <a routerLink="/trip-list" class="nav-link">View Trips</a>
        </li>
        <li class="nav-item">
          <a routerLink="/generate-itinerary" class="nav-link">AI Itinerary</a>
        </li>
        <li class="nav-item">
          <a routerLink="/Ai-trip-list" class="nav-link">AI Saved Trips</a>
        </li>
      </ul>

      <!-- Profile Icon & Dropdown -->
      <div class="dropdown text-end">
        <a href="#" (click)="toggleDropdown()" class="d-block link-light text-decoration-none" id="dropdownUser1">
          <img src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" alt="mdo" width="40" height="40" class="rounded-circle">
        </a>
        <div class="dropdown-menu dropdown-menu-end text-dark shadow animate__animated animate__fadeIn" *ngIf="showDropdown" style="min-width: 220px;">
          <div class="px-3 py-2">
            <p class="mb-1"><strong>User:</strong> {{ username }}</p>
            <p class="mb-2"><strong>Email:</strong> {{ email }}</p>
            <button class="btn btn-sm btn-danger w-100" (click)="logout()">
              <i class="bi bi-box-arrow-right me-1"></i> Logout
            </button>
          </div>
        </div>
      </div>
    </div>
  </div>
</nav>
