import { Component } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html'
})
export class RegisterComponent {
  user = {
    username: '',
    email: '',
    password: '',
    role: 'USER'
  };

  constructor(private authService: AuthService, private router: Router) {}

  registerUser() {
    this.authService.register(this.user).subscribe({
      next: res => {
        alert('Registration successful!');
        this.router.navigate(['/login']);
      },
      error: err => {
        alert('Registration failed!');
        console.error(err);
      }
    });
  }
}
<div class="container mt-5 col-md-6 animate__animated animate__fadeIn">
  <div class="card shadow-lg rounded-4 border-0">
    <div class="card-body p-4">
      <h2 class="text-center mb-4 fw-bold text-primary">
        <i class="bi bi-person-plus-fill me-2"></i>Register
      </h2>

      <form (submit)="registerUser()" novalidate>
        <div class="form-group mb-3">
          <label class="fw-semibold"><i class="bi bi-person-circle me-2"></i>Username</label>
          <input [(ngModel)]="user.username" name="username" class="form-control form-control-lg shadow-sm" required>
        </div>

        <div class="form-group mb-3">
          <label class="fw-semibold"><i class="bi bi-envelope-at-fill me-2"></i>Email</label>
          <input [(ngModel)]="user.email" name="email" type="email" class="form-control form-control-lg shadow-sm" required>
        </div>

        <div class="form-group mb-3">
          <label class="fw-semibold"><i class="bi bi-lock-fill me-2"></i>Password</label>
          <input [(ngModel)]="user.password" name="password" type="password" class="form-control form-control-lg shadow-sm" required>
        </div>

        <div class="form-group mb-4">
          <label class="fw-semibold"><i class="bi bi-person-badge-fill me-2"></i>Role</label>
          <select [(ngModel)]="user.role" name="role" class="form-select form-select-lg shadow-sm">
            <option value="USER">User</option>
            <option value="ADMIN">Admin</option>
          </select>
        </div>

        <button class="btn btn-primary w-100 btn-lg rounded-pill shadow-sm" type="submit">
          <i class="bi bi-check-circle-fill me-1"></i>Register
        </button>
      </form>

      <div class="mt-4 text-center">
        <p class="mb-0">Already have an account? 
          <a routerLink="/login" class="text-decoration-none fw-semibold text-primary hover-underline">
            <i class="bi bi-box-arrow-in-right me-1"></i>Login here
          </a>
        </p>
      </div>
    </div>
  </div>
</div>
