// src/app/components/admin-user-list/admin-user-list.component.ts
import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface User {
  id: number;
  username: string;
  email: string;
  role: string;
}

@Component({
  selector: 'app-admin-user-list',
  templateUrl: './admin-user-list.component.html'
})
export class AdminUserListComponent implements OnInit {
  users: User[] = [];
  selectedUser: User | null = null;
  isEditMode: boolean = false;

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.fetchUsers();
  }

  fetchUsers(): void {
    this.http.get<User[]>('http://localhost:8084/api/admin/users').subscribe({
      next: data => this.users = data,
      error: err => console.error('Error fetching users:', err)
    });
  }

  editUser(user: User): void {
    this.selectedUser = { ...user };
    this.isEditMode = true;
  }

  updateUser(): void {
    if (this.selectedUser) {
      this.http.put(`http://localhost:8084/api/admin/users/${this.selectedUser.id}`, this.selectedUser)
        .subscribe({
          next: () => {
            this.isEditMode = false;
            this.selectedUser = null;
            this.fetchUsers();
            alert('User updated successfully!');
          },
          error: err => {
            console.error('Error updating user:', err);
            alert('Failed to update user.');
          }
        });
    }
  }

  deleteUser(id: number): void {
    if (confirm('Are you sure you want to delete this user?')) {
      this.http.delete(`http://localhost:8084/api/admin/users/${id}`, { responseType: 'text' }).subscribe({
        next: () => {
          this.fetchUsers();
          alert('User deleted successfully!');
        },
        error: err => {
          console.error('Error deleting user:', err);
          alert('Failed to delete user.');
        }
      });
    }
  }

  cancelEdit(): void {
    this.selectedUser = null;
    this.isEditMode = false;
  }
}
<app-admin-navbar></app-admin-navbar>

<div class="container mt-4">
  <h2>Manage Users</h2>

  <!-- Edit Form -->
  <div *ngIf="isEditMode && selectedUser" class="card p-3 mt-3">
    <h4>Edit User</h4>
    <form (ngSubmit)="updateUser()">
      <div class="form-group mb-2">
        <label>Username</label>
        <input [(ngModel)]="selectedUser.username" name="username" class="form-control" required>
      </div>
      <div class="form-group mb-2">
        <label>Email</label>
        <input [(ngModel)]="selectedUser.email" name="email" class="form-control" type="email" required>
      </div>
      <div class="form-group mb-2">
        <label>Role</label>
        <select [(ngModel)]="selectedUser.role" name="role" class="form-control">
          <option value="USER">User</option>
          <option value="ADMIN">Admin</option>
        </select>
      </div>
      <button class="btn btn-success mt-2" type="submit">Update</button>
      <button class="btn btn-secondary mt-2 ms-2" (click)="cancelEdit()" type="button">Cancel</button>
    </form>
  </div>

  <!-- User Table -->
  <table class="table table-striped mt-4">
    <thead>
      <tr>
        <th>#</th>
        <th>Username</th>
        <th>Email</th>
        <th>Role</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let user of users; let i = index">
        <td>{{ i + 1 }}</td>
        <td>{{ user.username }}</td>
        <td>{{ user.email }}</td>
        <td>{{ user.role }}</td>
        <td>
          <button class="btn btn-sm btn-warning me-2" (click)="editUser(user)">Edit</button>
          <button class="btn btn-sm btn-danger" (click)="deleteUser(user.id)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
