import { Component } from '@angular/core';

@Component({
  selector: 'app-ai-itinerary',
  templateUrl: './ai-itinerary.component.html',
})
export class AiItineraryComponent {
  destination: string = '';
  startDate: string = '';
  endDate: string = '';
  itinerary: string | null = null;

  generateItinerary() {
    // Simulated AI generation (replace with API later)
    this.itinerary = `Here is your AI-generated itinerary for ${this.destination} 
from ${this.startDate} to ${this.endDate}:
- Day 1: Arrival and local sightseeing
- Day 2: Explore major attractions
- Day 3: Shopping and leisure
- Day 4: Departure`;
  }
}
<app-user-navbar></app-user-navbar>

<div class="container mt-4">
  <h2>AI Trip Itinerary Generator</h2>

  <form (ngSubmit)="generateItinerary()">
    <div class="mb-3">
      <label class="form-label">Destination</label>
      <input class="form-control" [(ngModel)]="destination" name="destination" required />
    </div>

    <div class="mb-3">
      <label class="form-label">Start Date</label>
      <input type="date" class="form-control" [(ngModel)]="startDate" name="startDate" required />
    </div>

    <div class="mb-3">
      <label class="form-label">End Date</label>
      <input type="date" class="form-control" [(ngModel)]="endDate" name="endDate" required />
    </div>

    <button type="submit" class="btn btn-primary">Generate</button>
  </form>

  <div *ngIf="itinerary" class="mt-4">
    <h4>Generated Itinerary:</h4>
    <pre>{{ itinerary }}</pre>
  </div>
</div>
