import { Component, OnInit } from '@angular/core';
import { TripService } from 'src/app/services/trip.service';
import { Trip } from 'src/app/models/trip.model';
import { Router } from '@angular/router';

@Component({
  selector: 'app-admin-trip-list',
  templateUrl: './admin-trip-list.component.html',
  styleUrls: ['./admin-trip-list.component.css']
})
export class AdminTripListComponent implements OnInit {
  trips: Trip[] = [];

  constructor(private tripService: TripService, private router: Router) {}

  ngOnInit(): void {
    this.loadAllTrips();
  }

  loadAllTrips(): void {
    this.tripService.getAllTrips().subscribe(data => {
      this.trips = data;
    });
  }

  deleteTrip(id: number): void {
    if (confirm('Are you sure you want to delete this trip?')) {
      this.tripService.deleteTrip(id).subscribe(() => this.loadAllTrips());
    }
  }
}
<app-admin-navbar></app-admin-navbar>

<div class="container py-4 animate__animated animate__fadeIn">
  <h2 class="mb-4 text-primary d-flex align-items-center gap-2">
    <i class="bi bi-globe2"></i>
    All User Trips
  </h2>

  <div *ngIf="trips?.length > 0; else noTrips">
    <div class="row g-4">
      <div class="col-md-6 col-lg-4" *ngFor="let trip of trips">
        <div class="card h-100 shadow-sm border-0">
          <div class="card-body">
            <h5 class="card-title text-secondary mb-2">
              <i class="bi bi-geo-alt-fill me-1 text-danger"></i>{{ trip.destination }}
            </h5>
            <p class="mb-1"><strong>Trip Name:</strong> {{ trip.tripName }}</p>
            <p class="mb-1"><strong>User:</strong> {{ trip.userEmail }}</p>
            <p class="mb-1">
              <strong>Start:</strong> {{ trip.startDate }}<br />
              <strong>End:</strong> {{ trip.endDate }}
            </p>
            <p class="mb-1">
              <strong>Budget:</strong> <span class="badge bg-success">₹{{ trip.budget | number:'1.0-0' }}</span>
            </p>
            <p class="mb-1">
              <strong>Transport:</strong> <span class="badge bg-info text-dark">{{ trip.transportation }}</span>
            </p>
            <p class="mb-2"><strong>Notes:</strong> {{ trip.notes || '-' }}</p>
          </div>
          <div class="card-footer bg-transparent border-0 d-flex justify-content-end">
            <button class="btn btn-outline-danger btn-sm" (click)="deleteTrip(trip.id!)">
              <i class="bi bi-trash-fill me-1"></i> Delete
            </button>
          </div>
        </div>
      </div>
    </div>
  </div>

  <!-- Empty state -->
  <ng-template #noTrips>
    <div class="alert alert-info text-center mt-5">
      <i class="bi bi-emoji-frown me-2"></i>No trips available to display.
    </div>
  </ng-template>
</div>
