import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-admin-navbar',
  templateUrl: './admin-navbar.component.html'
})
export class AdminNavbarComponent {
  showDropdown = false;
  menuOpen = false;
  username = localStorage.getItem('name');
  email = localStorage.getItem('email');

  constructor(private authService: AuthService, private router: Router) {}

  toggleDropdown(): void {
    this.showDropdown = !this.showDropdown;
  }

  toggleMenu(): void {
    this.menuOpen = !this.menuOpen;
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<nav class="navbar navbar-expand-lg navbar-dark bg-primary px-4 py-2 shadow-sm">
  <div class="container-fluid">
    <span class="navbar-brand fw-bold">Admin Dashboard</span>

    <!-- Toggler for mobile -->
    <button class="navbar-toggler" type="button" (click)="toggleMenu()">
      <span class="navbar-toggler-icon"></span>
    </button>

    <!-- Collapsible menu -->
    <div [ngClass]="{ 'collapse navbar-collapse': !menuOpen, 'navbar-collapse': true }">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item">
          <a routerLink="/admin/users" class="nav-link d-flex align-items-center gap-1">
            <i class="bi bi-person-gear"></i> Manage Users
          </a>
        </li>
        <li class="nav-item">
          <a routerLink="/admin/trips" class="nav-link d-flex align-items-center gap-1">
            <i class="bi bi-luggage-fill"></i> Manage Trips
          </a>
        </li>
      </ul>

      <!-- Profile dropdown -->
      <div class="dropdown">
        <img src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" alt="Profile"
             class="rounded-circle" width="40" height="40" role="button"
             (click)="toggleDropdown()" style="cursor: pointer;" />

        <div *ngIf="showDropdown"
             class="dropdown-menu dropdown-menu-end show mt-2 p-3 shadow border-0"
             style="min-width: 220px;">
          <p class="mb-1"><strong>User:</strong> {{ username }}</p>
          <p class="mb-2"><strong>Email:</strong> {{ email }}</p>
          <button (click)="logout()" class="btn btn-danger btn-sm w-100">Logout</button>
        </div>
      </div>
    </div>
  </div>
</nav>
