// src/app/components/trip-list/trip-list.component.ts

import { Component, OnInit } from '@angular/core';
import { TripService, Trip } from 'src/app/services/trip.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-trip-list',
  templateUrl: './trip-list.component.html'
})
export class TripListComponent implements OnInit {
  trips: Trip[] = [];

  constructor(
    private tripService: TripService,
    private router: Router
  ) {}

  ngOnInit(): void {
    const email = localStorage.getItem('userEmail') || '';
    this.tripService.getTripsByEmail(email).subscribe(data => {
      this.trips = data;
    });
  }

  editTrip(id: number): void {
    this.router.navigate(['/edit-trip', id]);
  }

  deleteTrip(id: number): void {
    if (confirm('Are you sure you want to delete this trip?')) {
      this.tripService.deleteTrip(id).subscribe(() => {
        this.trips = this.trips.filter(trip => trip.id !== id);
      });
    }
  }
}
<app-user-navbar></app-user-navbar>

<div class="container mt-4">
  <h2>Your Trips</h2>
  <a routerLink="/add-trip" class="btn btn-primary mb-3">Add New Trip</a>

  <table class="table table-bordered">
    <thead>
      <tr>
        <th>Name</th>
        <th>Destination</th>
        <th>Start</th>
        <th>End</th>
        <th>Notes</th>
        <th>Email</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let trip of trips">
        <td>{{ trip.tripName }}</td>
        <td>{{ trip.destination }}</td>
        <td>{{ trip.startDate }}</td>
        <td>{{ trip.endDate }}</td>
        <td>{{ trip.notes }}</td>
        <td>{{ trip.userEmail }}</td>
        <td>
          <button class="btn btn-warning btn-sm" (click)="editTrip(trip)">Edit</button>
          <button class="btn btn-danger btn-sm" (click)="deleteTrip(trip.id)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
