import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router'; // ✅ import Router
import { AiTrip } from 'src/app/models/ai-trip.model';
import { AiTripService } from 'src/app/services/ai-trip.service';

@Component({
  selector: 'app-ai-trip-list',
  templateUrl: './ai-trip-list.component.html',
  styleUrls: ['./ai-trip-list.component.css']
})
export class AiTripListComponent implements OnInit {
  trips: AiTrip[] = [];
  isLoading = true;

  constructor(
    private tripService: AiTripService,
    private router: Router // ✅ inject Router
  ) {}

  ngOnInit(): void {
    this.tripService.getAllTrips().subscribe({
      next: (data) => {
        this.trips = data;
        this.isLoading = false;
      },
      error: (err) => {
        console.error('Failed to load trips', err);
        this.isLoading = false;
      }
    });
  }

  // ✅ Navigate to details page
  viewTrip(id: number) {
    this.router.navigate(['/Ai-trip-details', id]);
  }

  editTrip(id: number) {
  this.router.navigate(['/edit-ai-trip', id]); // You’ll need to create this route/component
}

deleteTrip(id: number) {
  if (confirm('Are you sure you want to delete this trip?')) {
    this.tripService.deleteTrip(id).subscribe({
      next: () => {
        alert('Trip deleted successfully');
        this.trips = this.trips.filter(t => t.id !== id); // remove deleted trip from list
      },
      error: (err) => {
        console.error('Failed to delete trip', err);
        alert('Failed to delete trip');
      }
    });
  }
}


}
<app-user-navbar></app-user-navbar>

<div class="container mt-4 mb-5">
  <h2 class="text-center mb-4 text-primary">
    <i class="bi bi-map-fill me-2"></i>Your AI Planned Trips
  </h2>

  <div *ngIf="trips.length === 0" class="alert alert-info text-center">
    <i class="bi bi-info-circle-fill me-2"></i>No trips found. Plan your first trip!
  </div>

  <div *ngFor="let trip of trips" class="card shadow-sm mb-3 animate__animated animate__fadeIn">
    <div class="card-body d-flex flex-column flex-md-row justify-content-between align-items-md-center">
      <div>
        <h5 class="card-title text-success">
          <i class="bi bi-geo-alt-fill me-1"></i>{{ trip.destination }}
        </h5>
        <p class="card-text mb-2 mb-md-0 text-muted">
          <i class="bi bi-calendar-event me-1"></i>
          {{ trip.startDate | date:'mediumDate' }} - {{ trip.endDate | date:'mediumDate' }}
        </p>
      </div>

      <div class="btn-group mt-2 mt-md-0">
        <button class="btn btn-outline-primary me-2" (click)="viewTrip(trip.id)">
          <i class="bi bi-eye"></i> View
        </button>
        <button class="btn btn-outline-warning me-2" (click)="editTrip(trip.id)">
          <i class="bi bi-pencil-square"></i> Edit
        </button>
        <button class="btn btn-outline-danger" (click)="deleteTrip(trip.id)">
          <i class="bi bi-trash-fill"></i> Delete
        </button>
      </div>
    </div>
  </div>
</div>
