import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-admin-dashboard',
  templateUrl: './admin-dashboard.component.html'
})
export class AdminDashboardComponent implements OnInit {
  showDropdown = false;
  username: string | null = '';
  email: string | null = '';

  constructor(private authService: AuthService, private router: Router) {}

  ngOnInit(): void {
    this.username = localStorage.getItem('name');
    this.email = localStorage.getItem('email');
  }

  toggleDropdown(): void {
    this.showDropdown = !this.showDropdown;
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<!-- Navbar -->
<nav style="display: flex; justify-content: space-between; align-items: center; padding: 10px 20px; background-color: #1976d2; color: white;">
  <div style="font-size: 20px;">Admin Dashboard</div>

  <!-- Profile Icon -->
  <div style="position: relative;">
    <img
      src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png"
      alt="Profile"
      (click)="toggleDropdown()"
      style="width: 40px; height: 40px; border-radius: 50%; cursor: pointer;"
    />
    <!-- Dropdown -->
    <div *ngIf="showDropdown"
         style="position: absolute; right: 0; top: 50px; background: white; color: black; border: 1px solid #ccc; border-radius: 8px; padding: 12px; min-width: 220px; z-index: 100;">
      <p><strong>User:</strong> {{ username }}</p>
      <p><strong>Email:</strong> {{ email }}</p>
      <button (click)="logout()"
              style="background-color: #d32f2f; color: white; padding: 6px 12px; border: none; border-radius: 4px; cursor: pointer;">
        Logout
      </button>
    </div>
  </div>
</nav>

<!-- Page Content -->
<div style="padding: 20px;">
  <h2>Welcome, {{ username }}!</h2>
  <p>This is the Admin dashboard.</p>
</div>
