import { Component, OnInit } from '@angular/core';
import { Trip, TripService } from 'src/app/services/trip.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-add-trip',
  templateUrl: './add-trip.component.html',
})
export class AddTripComponent implements OnInit {
  trip: Trip = {
    tripName: '',
    destination: '',
    startDate: '',
    endDate: '',
    notes: '',
    userEmail: '',
  };

  isEditMode = false;
  tripId: number = 0;

  constructor(
    private tripService: TripService,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    const editData = history.state.trip;
    if (editData) {
      this.trip = editData;
      this.tripId = editData.id;
      this.isEditMode = true;
    } else {
      const email = localStorage.getItem('userEmail');
      if (email) this.trip.userEmail = email;
    }
  }

  onSubmit(): void {
    if (this.isEditMode) {
      this.tripService.updateTrip(this.tripId, this.trip).subscribe(() => {
        alert('Trip updated!');
        this.router.navigate(['/trip-list']);
      });
    } else {
      this.tripService.addTrip(this.trip).subscribe(() => {
        alert('Trip added!');
        this.router.navigate(['/trip-list']);
      });
    }
  }
}
<app-user-navbar></app-user-navbar>

<div class="container mt-4">
  <h2>{{ isEditMode ? 'Edit Trip' : 'Add Trip' }}</h2>

  <form (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label>Trip Name</label>
      <input type="text" class="form-control" [(ngModel)]="trip.tripName" name="tripName" required />
    </div>

    <div class="form-group">
      <label>Destination</label>
      <input type="text" class="form-control" [(ngModel)]="trip.destination" name="destination" required />
    </div>

    <div class="form-group">
      <label>Start Date</label>
      <input type="date" class="form-control" [(ngModel)]="trip.startDate" name="startDate" required />
    </div>

    <div class="form-group">
      <label>End Date</label>
      <input type="date" class="form-control" [(ngModel)]="trip.endDate" name="endDate" required />
    </div>

    <div class="form-group">
      <label>Notes</label>
      <textarea class="form-control" [(ngModel)]="trip.notes" name="notes"></textarea>
    </div>

    <button class="btn btn-success mt-3">{{ isEditMode ? 'Update Trip' : 'Add Trip' }}</button>
  </form>
</div>
