import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-generate-itinerary',
  templateUrl: './generate-itinerary.component.html'
})
export class GenerateItineraryComponent {
  destination: string = '';
  startDate: string = '';
  endDate: string = '';
  rawResult: string = '';
  translatedResult: string = '';
  isGenerated: boolean = false;
  isLoading: boolean = false;

  constructor(private http: HttpClient, private router: Router) {}

  generateItinerary() {
    if (!this.destination || !this.startDate || !this.endDate) {
      alert('Please fill all fields.');
      return;
    }

    this.isLoading = true;

    const requestData = {
      destination: this.destination,
      startDate: this.startDate,
      endDate: this.endDate
    };

    this.http.post('http://localhost:8084/api/ai/generate-itinerary', requestData, { responseType: 'text' }).subscribe({
      next: (response: any) => {
        this.rawResult = response;
        this.isGenerated = true;
        this.isLoading = false;
      },
      error: (err) => {
        console.error(err);
        alert('AI generation failed');
        this.isLoading = false;
      }
    });
  }

  convertToHtml(text: string): string {
    // Wrap plain text into <p> and <strong> blocks for formatting
    const paragraphs = text.split('\n').filter(p => p.trim() !== '');
    return paragraphs.map(p => `<p>${p}</p>`).join('');
  }

  saveTrip() {
    const finalText = this.translatedResult || this.rawResult;
    const htmlFormatted = this.convertToHtml(finalText);
    const userEmail = localStorage.getItem('email');

    const tripData = {
      destination: this.destination,
      startDate: this.startDate,
      endDate: this.endDate,
      itinerary: htmlFormatted,
      userEmail: userEmail
    };

    this.http.post('http://localhost:8084/api/ai-trips/save', tripData, { responseType: 'text' }).subscribe({
      next: () => {
        alert('Trip saved successfully!');
        this.router.navigate(['/Ai-trip-list']);
      },
      error: (err) => {
        console.error('Save Trip Error:', err);
        alert('Failed to save trip.');
      }
    });
  }
}
