<app-user-navbar></app-user-navbar>

<div class="container mt-5" style="max-width: 800px;">
  <h2 class="mb-4">AI Trip Itinerary Generator</h2>

  <form (ngSubmit)="generateItinerary()" *ngIf="!isGenerated">
    <div class="form-group mb-3">
      <label>Destination:</label>
      <input type="text" class="form-control" [(ngModel)]="destination" name="destination" required>
    </div>

    <div class="form-group mb-3">
      <label>Start Date:</label>
      <input type="date" class="form-control" [(ngModel)]="startDate" name="startDate" required>
    </div>

    <div class="form-group mb-3">
      <label>End Date:</label>
      <input type="date" class="form-control" [(ngModel)]="endDate" name="endDate" required>
    </div>

    <button type="submit" class="btn btn-primary" [disabled]="isLoading">
      {{ isLoading ? 'Generating...' : 'Generate Itinerary' }}
    </button>
  </form>

  <div *ngIf="isGenerated" class="mt-4">
    <h4>Generated Itinerary:</h4>
    <div class="border p-3 bg-light" [innerHTML]="convertToHtml(translatedResult || rawResult)"></div>

    <div class="text-end mt-3">
      <button class="btn btn-success" (click)="saveTrip()">Save This Trip</button>
    </div>
  </div>
</div>
