import { Component, OnInit } from '@angular/core';
import { TripService } from 'src/app/services/trip.service';
import { Trip } from 'src/app/models/trip.model';
import { Router } from '@angular/router';

@Component({
  selector: 'app-admin-trip-list',
  templateUrl: './admin-trip-list.component.html',
  styleUrls: ['./admin-trip-list.component.css']
})
export class AdminTripListComponent implements OnInit {
  trips: Trip[] = [];

  constructor(private tripService: TripService, private router: Router) {}

  ngOnInit(): void {
    this.loadAllTrips();
  }

  loadAllTrips(): void {
    this.tripService.getAllTrips().subscribe(data => {
      this.trips = data;
    });
  }

  deleteTrip(id: number): void {
    if (confirm('Are you sure you want to delete this trip?')) {
      this.tripService.deleteTrip(id).subscribe(() => this.loadAllTrips());
    }
  }
}
<app-admin-navbar></app-admin-navbar>

<div class="container mt-4">
  <h2>All User Trips</h2>

  <table class="table table-bordered table-hover mt-3">
    <thead class="thead-dark">
      <tr>
        <th>User Email</th>
        <th>Trip Name</th>
        <th>Destination</th>
        <th>Start</th>
        <th>End</th>
        <th>Budget</th>
        <th>Transport</th>
        <th>Notes</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let trip of trips">
        <td>{{ trip.userEmail }}</td>
        <td>{{ trip.tripName }}</td>
        <td>{{ trip.destination }}</td>
        <td>{{ trip.startDate }}</td>
        <td>{{ trip.endDate }}</td>
        <td>{{ trip.budget }}</td>
        <td>{{ trip.transportation }}</td>
        <td>{{ trip.notes }}</td>
        <td>
          <button class="btn btn-danger btn-sm" (click)="deleteTrip(trip.id!)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
