import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService, RegisterRequest } from 'src/app/services/auth.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
})
export class RegisterComponent {
  registerRequest: RegisterRequest = {
    name: '',
    email: '',
    password: '',
    role: 'USER' // default role
  };

  constructor(private authService: AuthService, private router: Router) {}

  register(): void {
    this.authService.register(this.registerRequest).subscribe({
      next: () => {
        alert('Registration successful!');
        this.router.navigate(['/login']);
      },
      error: () => {
        alert('Registration failed. Try again.');
      }
    });
  }
}
<div class="container mt-5 col-md-6">
  <h2>Register</h2>
  <form (submit)="registerUser()">
    <div class="form-group mb-2">
      <label>Username</label>
      <input [(ngModel)]="user.username" name="username" class="form-control" required>
    </div>
    <div class="form-group mb-2">
      <label>Email</label>
      <input [(ngModel)]="user.email" name="email" type="email" class="form-control" required>
    </div>
    <div class="form-group mb-2">
      <label>Password</label>
      <input [(ngModel)]="user.password" name="password" type="password" class="form-control" required>
    </div>
    <div class="form-group mb-2">
      <label>Role</label>
      <select [(ngModel)]="user.role" name="role" class="form-control">
        <option value="USER">User</option>
        <option value="ADMIN">Admin</option>
      </select>
    </div>
    <button class="btn btn-primary mt-2" type="submit">Register</button>
  </form>

  <!-- ✅ Login Link -->
  <div class="mt-3">
    <p>Already have an account? 
      <a routerLink="/login" class="text-primary">Login here</a>
    </p>
  </div>
</div>
