import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { DomSanitizer, SafeHtml } from '@angular/platform-browser';

@Component({
  selector: 'app-generate-itinerary',
  templateUrl: './generate-itinerary.component.html',
})
export class GenerateItineraryComponent {
  destination = '';
  startDate = '';
  endDate = '';
  rawResult: string = '';
  translatedResult: string = '';
  safeHtmlResult: SafeHtml = '';
  isLoading: boolean = false;
  selectedLanguage = 'en';

  weatherData: any = null;

  constructor(private http: HttpClient, private sanitizer: DomSanitizer) {}

  generateItinerary() {
    this.rawResult = '';
    this.safeHtmlResult = '';
    this.weatherData = null;
    this.isLoading = true;

    const body = {
      destination: this.destination,
      startDate: this.startDate,
      endDate: this.endDate,
    };

    this.http.post<any>('http://localhost:8089/api/gemini/generate-itinerary', body).subscribe({
      next: (res) => {
        this.rawResult = res.itinerary;
        this.getWeather(this.destination); // Get weather after itinerary
      },
      error: (err) => {
        console.error('Itinerary Error:', err);
        this.rawResult = 'An error occurred while generating the itinerary.';
        this.safeHtmlResult = this.rawResult;
        this.isLoading = false;
      },
    });
  }

  getWeather(city: string) {
    const apiKey = 'cf4280e39d29669fba3fcbd9bfc61b0c';
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;

    this.http.get<any>(url).subscribe({
      next: (data) => {
        this.weatherData = data;
        this.translateItinerary(this.rawResult, this.selectedLanguage);
      },
      error: (err) => {
        console.error('Weather Error:', err);
        this.weatherData = null;
        this.translateItinerary(this.rawResult, this.selectedLanguage);
      }
    });
  }

  translateItinerary(text: string, targetLang: string) {
    if (targetLang === 'en') {
      this.translatedResult = text;
      this.safeHtmlResult = this.sanitizer.bypassSecurityTrustHtml(this.convertToHtml(text));
      this.isLoading = false;
      return;
    }

    const url = `https://libretranslate.de/translate`;
    const body = {
      q: text,
      source: 'en',
      target: targetLang,
      format: 'text'
    };

    this.http.post<any>(url, body).subscribe({
      next: (res) => {
        const translatedText = res.translatedText;
        this.translatedResult = translatedText;
        this.safeHtmlResult = this.sanitizer.bypassSecurityTrustHtml(this.convertToHtml(translatedText));
        this.isLoading = false;
      },
      error: (err) => {
        console.error('Translation Error:', err);
        this.translatedResult = text;
        this.safeHtmlResult = this.convertToHtml(text);
        this.isLoading = false;
      },
    });
  }

  private convertToHtml(markdown: string): string {
    let html = markdown;
    html = html.replace(/^## (.*$)/gim, '<h3>$1</h3>');
    html = html.replace(/^# (.*$)/gim, '<h2>$1</h2>');
    html = html.replace(/\*\*(.*?)\*\*/gim, '<strong>$1</strong>');
    html = html.replace(/\*(.*?)\*/gim, '<em>$1</em>');
    html = html.replace(/^\* (.*$)/gim, '<li>$1</li>');
    html = html.replace(/(<li>[\s\S]*?<\/li>)/gim, '<ul>$1</ul>');
    html = html.replace(/\n/g, '<br>');
    return html.trim();
  }
}
<app-user-navbar></app-user-navbar>

<div style="max-width: 700px; margin: auto; padding: 20px;">
  <h2>Generate Travel Itinerary</h2>

  <div class="mb-3">
    <input type="text" [(ngModel)]="destination" placeholder="Destination" class="form-control" />
  </div>

  <div class="mb-3">
    <input type="date" [(ngModel)]="startDate" class="form-control" />
  </div>

  <div class="mb-3">
    <input type="date" [(ngModel)]="endDate" class="form-control" />
  </div>

  <div class="mb-3">
    <label for="languageSelect">Choose Language:</label>
    <select class="form-control" id="languageSelect" [(ngModel)]="selectedLanguage">
      <option value="en">English</option>
      <option value="hi">Hindi (हिंदी)</option>
      <option value="mr">Marathi (मराठी)</option>
    </select>
  </div>

  <button (click)="generateItinerary()" class="btn btn-primary">Generate</button>

  <div *ngIf="isLoading" style="margin-top: 20px;">
    <p><strong>Generating your itinerary...</strong></p>
  </div>

  <div *ngIf="!isLoading && weatherData" class="mt-4 p-3 border rounded bg-light">
    <h4>Current Weather in {{ weatherData.name }}:</h4>
    <p><strong>Temperature:</strong> {{ weatherData.main.temp }}°C</p>
    <p><strong>Condition:</strong> {{ weatherData.weather[0].description }}</p>
    <p><strong>Humidity:</strong> {{ weatherData.main.humidity }}%</p>
  </div>

  <div *ngIf="!isLoading && safeHtmlResult" style="margin-top: 20px;">
    <h4>Generated Itinerary:</h4>
    <div [innerHTML]="safeHtmlResult" class="border p-3 rounded bg-light"></div>
  </div>
</div>
