import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-add-trip',
  templateUrl: './add-trip.component.html',
})
export class AddTripComponent {
  trip = {
    tripName: '',
    destination: '',
    startDate: '',
    endDate: '',
    budget: 0,
    notes: ''
  };

  generatedItinerary: string = '';

  constructor(private http: HttpClient) {}

  submitTrip() {
    this.http.post('http://localhost:8089/api/trips', this.trip).subscribe(
      () => {
        alert('Trip saved successfully!');
      },
      error => {
        console.error('Error:', error);
      }
    );
  }

  generateItinerary() {
    const prompt = `Create a travel itinerary titled "${this.trip.tripName}" for ${this.trip.destination} from ${this.trip.startDate} to ${this.trip.endDate} within ₹${this.trip.budget}.`;

    this.http.post('http://localhost:8089/api/ai/itinerary', { prompt }, { responseType: 'text' }).subscribe(
      response => {
        this.generatedItinerary = response;
      },
      error => {
        console.error('AI Error:', error);
      }
    );
  }
}
<div class="container mt-5 col-md-6">
  <h2 class="mb-3">Plan Your Trip</h2>

  <form (ngSubmit)="submitTrip()">
    <div class="form-group mb-2">
      <label>Trip Name</label>
      <input type="text" class="form-control" [(ngModel)]="trip.tripName" name="tripName" required>
    </div>

    <div class="form-group mb-2">
      <label>Destination</label>
      <input type="text" class="form-control" [(ngModel)]="trip.destination" name="destination" required>
    </div>

    <div class="form-group mb-2">
      <label>Start Date</label>
      <input type="date" class="form-control" [(ngModel)]="trip.startDate" name="startDate" required>
    </div>

    <div class="form-group mb-2">
      <label>End Date</label>
      <input type="date" class="form-control" [(ngModel)]="trip.endDate" name="endDate" required>
    </div>

    <div class="form-group mb-2">
      <label>Budget (₹)</label>
      <input type="number" class="form-control" [(ngModel)]="trip.budget" name="budget" required>
    </div>

    <div class="form-group mb-3">
      <label>Notes</label>
      <textarea class="form-control" [(ngModel)]="trip.notes" name="notes" rows="3"></textarea>
    </div>

    <button type="submit" class="btn btn-success">Save Trip</button>
    <button type="button" class="btn btn-info ms-2" (click)="generateItinerary()">Generate AI Itinerary</button>
  </form>

  <div *ngIf="generatedItinerary" class="mt-4">
    <h4>AI-Generated Itinerary</h4>
    <pre>{{ generatedItinerary }}</pre>
  </div>
</div>
