import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';

export interface Trip {
  id?: number;
  tripName: string;
  destination: string;
  startDate: string;
  endDate: string;
  budget: number;
  notes: string;
  userEmail?: string;
}

@Injectable({
  providedIn: 'root'
})
export class TripService {
  private apiUrl = 'http://localhost:8089/api/trips';

  constructor(private http: HttpClient) {}

  getAllTrips(): Observable<Trip[]> {
    const email = localStorage.getItem('email');
    return this.http.get<Trip[]>(`${this.apiUrl}/user/${email}`);
  }

  getTripById(id: number): Observable<Trip> {
    return this.http.get<Trip>(`${this.apiUrl}/${id}`);
  }

  addTrip(trip: Trip): Observable<Trip> {
    trip.userEmail = localStorage.getItem('email') || '';
    return this.http.post<Trip>(`${this.apiUrl}`, trip);
  }

  updateTrip(id: number, trip: Trip): Observable<Trip> {
    return this.http.put<Trip>(`${this.apiUrl}/${id}`, trip);
  }

  deleteTrip(id: number): Observable<void> {
    return this.http.delete<void>(`${this.apiUrl}/${id}`);
  }
}
