import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-admin-dashboard',
  templateUrl: './admin-dashboard.component.html'
})
export class AdminDashboardComponent implements OnInit {
  showDropdown = false;
  username: string | null = '';
  email: string | null = '';

  constructor(private authService: AuthService, private router: Router) {}

  ngOnInit(): void {
    this.username = localStorage.getItem('name');
    this.email = localStorage.getItem('email');
  }

  toggleDropdown(): void {
    this.showDropdown = !this.showDropdown;
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<app-admin-navbar></app-admin-navbar>

<div class="container py-4">
  <div class="row justify-content-center text-center">
    <div class="col-lg-8">
      <div class="card shadow-lg border-0 rounded-4 p-4 animate__animated animate__fadeIn">
        <div class="card-body">
          <h2 class="card-title text-primary mb-3">
            <i class="bi bi-person-circle me-2"></i>Welcome, {{ username }}!
          </h2>
          <p class="card-text fs-5">
            This is your <strong>Admin Dashboard</strong>. Use the navigation bar to manage users and trips.
          </p>
        </div>
      </div>
    </div>
  </div>
</div>
