import { Component, OnInit } from '@angular/core';
import { TripService } from 'src/app/services/trip.service';
import { Trip } from 'src/app/models/trip.model';
import { Router } from '@angular/router';

@Component({
  selector: 'app-admin-trip-list',
  templateUrl: './admin-trip-list.component.html',
  styleUrls: ['./admin-trip-list.component.css']
})
export class AdminTripListComponent implements OnInit {
  trips: Trip[] = [];

  constructor(private tripService: TripService, private router: Router) {}

  ngOnInit(): void {
    this.loadAllTrips();
  }

  loadAllTrips(): void {
    this.tripService.getAllTrips().subscribe(data => {
      this.trips = data;
    });
  }

  deleteTrip(id: number): void {
    if (confirm('Are you sure you want to delete this trip?')) {
      this.tripService.deleteTrip(id).subscribe(() => this.loadAllTrips());
    }
  }
}
<app-admin-navbar></app-admin-navbar>

<div class="container py-4 animate__animated animate__fadeIn">
  <h2 class="mb-4 text-primary">
    <i class="bi bi-globe2 me-2"></i>All User Trips
  </h2>

  <div class="table-responsive">
    <table class="table table-striped table-hover align-middle shadow-sm">
      <thead class="table-dark">
        <tr>
          <th>User Email</th>
          <th>Trip Name</th>
          <th>Destination</th>
          <th>Start</th>
          <th>End</th>
          <th>Budget</th>
          <th>Transport</th>
          <th>Notes</th>
          <th class="text-center">Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let trip of trips">
          <td>{{ trip.userEmail }}</td>
          <td>{{ trip.tripName }}</td>
          <td>{{ trip.destination }}</td>
          <td>{{ trip.startDate }}</td>
          <td>{{ trip.endDate }}</td>
          <td>
            ₹{{ trip.budget | number:'1.0-0' }}
          </td>
          <td>{{ trip.transportation }}</td>
          <td>{{ trip.notes }}</td>
          <td class="text-center">
            <button class="btn btn-sm btn-danger" (click)="deleteTrip(trip.id!)">
              <i class="bi bi-trash-fill me-1"></i> Delete
            </button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
