// src/app/components/generate-itinerary/generate-itinerary.component.ts

import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-generate-itinerary',
  templateUrl: './generate-itinerary.component.html',
  styleUrls: ['./generate-itinerary.component.css']
})
export class GenerateItineraryComponent {
  destination: string = '';
  days: string = '';
  itinerary: string = '';
  showItinerary: boolean = false;
  userEmail: string = localStorage.getItem('userEmail') || '';

  constructor(private http: HttpClient, private router: Router) {}

  generateItinerary() {
    const request = {
      destination: this.destination,
      days: this.days
    };

    this.http.post('http://localhost:8089/api/gemini/generate-itinerary', request, { responseType: 'text' })
      .subscribe({
        next: (response: string) => {
          this.itinerary = response;
          this.showItinerary = true;
        },
        error: (error) => {
          console.error('Itinerary Error:', error);
        }
      });
  }

  saveItineraryAsTrip() {
    const tripData = {
      tripName: 'AI Planned Trip',
      destination: this.destination,
      startDate: new Date(), // you can customize this
      endDate: new Date(),   // you can customize this
      notes: this.itinerary,
      userEmail: this.userEmail
    };

    this.http.post('http://localhost:8089/api/trips', tripData)
      .subscribe({
        next: () => {
          alert('Trip saved successfully!');
          this.router.navigate(['/trips']);
        },
        error: (error) => {
          console.error('Save Trip Error:', error);
        }
      });
  }
}
<app-user-navbar></app-user-navbar>

<div style="max-width: 700px; margin: auto; padding: 20px;">
  <h2>Plan Your Trip</h2>

  <div class="mb-3">
    <input type="text" [(ngModel)]="destination" placeholder="Destination" class="form-control" />
  </div>

  <div class="mb-3">
    <input type="date" [(ngModel)]="startDate" class="form-control" />
  </div>

  <div class="mb-3">
    <input type="date" [(ngModel)]="endDate" class="form-control" />
  </div>

  <div class="mb-3">
    <label for="languageSelect">Choose Language:</label>
    <select class="form-control" id="languageSelect" [(ngModel)]="selectedLanguage">
      <option value="en">English</option>
      <option value="hi">Hindi (हिंदी)</option>
      <option value="mr">Marathi (मराठी)</option>
    </select>
  </div>

  <button (click)="generateItinerary()" class="btn btn-success me-2">Generate Itinerary</button>
  <button (click)="checkWeatherAndPacking()" class="btn btn-info">Check Weather & Packing List</button>

  <div *ngIf="isLoading" style="margin-top: 20px;">
    <p><strong>Processing...</strong></p>
  </div>

  <!-- Weather -->
  <div *ngIf="!isLoading && weatherData" class="mt-4 p-3 border rounded bg-light">
    <h4>Current Weather in {{ weatherData.name }}:</h4>
    <p><strong>Temperature:</strong> {{ weatherData.main.temp }}°C</p>
    <p><strong>Condition:</strong> {{ weatherData.weather[0].description }}</p>
    <p><strong>Humidity:</strong> {{ weatherData.main.humidity }}%</p>
  </div>

  <!-- Packing List -->
  <app-packing-list *ngIf="!isLoading && weatherData" [weatherData]="weatherData"></app-packing-list>

  <!-- Itinerary -->
  <div *ngIf="!isLoading && safeHtmlResult" style="margin-top: 20px;">
    <h4>Generated Itinerary:</h4>
    <div [innerHTML]="safeHtmlResult" class="border p-3 rounded bg-light"></div>
  </div>
</div>
