<app-user-navbar></app-user-navbar>

<div class="container mt-4">
  <h2>Saved AI Trips</h2>

  <div *ngIf="aiTrips.length === 0" class="alert alert-info mt-4">
    No trips found.
  </div>

  <div *ngFor="let trip of aiTrips" class="card mb-3">
    <div class="card-body">
      <h5 class="card-title">{{ trip.destination }}</h5>
      <p class="card-text">
        <strong>Start:</strong> {{ trip.startDate }}<br>
        <strong>End:</strong> {{ trip.endDate }}
      </p>
      <div [innerHTML]="trip.itinerary"></div>
    </div>
  </div>
</div>
