import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { AiTripService } from 'src/app/services/ai-trip.service';
import { AiTrip } from 'src/app/models/ai-trip.model';

@Component({
  selector: 'app-edit-ai-trip',
  templateUrl: './edit-ai-trip.component.html',
  styleUrls: ['./edit-ai-trip.component.css']
})
export class EditAiTripComponent implements OnInit {
  tripId!: number;
  trip: AiTrip = {
    id: 0,
    destination: '',
    days: 0,
    budget: '',
    travellers: '',
    tripPlan: ''
  };

  constructor(
    private route: ActivatedRoute,
    private tripService: AiTripService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.tripId = Number(this.route.snapshot.paramMap.get('id'));
    this.tripService.getTripById(this.tripId).subscribe({
      next: (data) => {
        this.trip = data;
      },
      error: (error: any) => {
        console.error('Error fetching trip:', error);
      }
    });
  }

  onUpdateTrip(): void {
    this.tripService.updateTrip(this.tripId, this.trip).subscribe({
      next: () => {
        this.router.navigate(['/saved-ai-trips']);
      },
      error: (err: any) => {
        console.error('Update failed', err);
      }
    });
  }
}
