// src/app/components/trip-list/trip-list.component.ts

import { Component, OnInit } from '@angular/core';
import { TripService, Trip } from 'src/app/services/trip.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-trip-list',
  templateUrl: './trip-list.component.html'
})
export class TripListComponent implements OnInit {
  trips: Trip[] = [];

  constructor(
    private tripService: TripService,
    private router: Router
  ) {}

  ngOnInit(): void {
    const email = localStorage.getItem('userEmail') || '';
    this.tripService.getTripsByEmail(email).subscribe(data => {
      this.trips = data;
    });
  }

  editTrip(id: number): void {
    this.router.navigate(['/edit-trip', id]);
  }

  deleteTrip(id: number): void {
    if (confirm('Are you sure you want to delete this trip?')) {
      this.tripService.deleteTrip(id).subscribe(() => {
        this.trips = this.trips.filter(trip => trip.id !== id);
      });
    }
  }
}
