<div class="container mt-4">
  <h2>Your Trips</h2>

  <div class="text-end mb-3">
    <a routerLink="/add-trip" class="btn btn-primary">+ Add New Trip</a>
  </div>

  <table class="table table-striped" *ngIf="trips.length > 0">
    <thead>
      <tr>
        <th>Trip Name</th>
        <th>Destination</th>
        <th>Start Date</th>
        <th>End Date</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let trip of trips">
        <td>{{ trip.tripName }}</td>
        <td>{{ trip.destination }}</td>
        <td>{{ trip.startDate }}</td>
        <td>{{ trip.endDate }}</td>
        <td>
          <button class="btn btn-sm btn-info me-2" (click)="editTrip(trip.id!)">Edit</button>
          <button class="btn btn-sm btn-danger" (click)="deleteTrip(trip.id!)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>

  <div *ngIf="trips.length === 0">
    <p>No trips found. Click "Add New Trip" to create one!</p>
  </div>
</div>
